alter table LapTime add constraint FKLapTime816787 foreign key (raceId) references Race (raceId);
alter table PitStop add constraint FKPitStop409811 foreign key (driverId) references Driver (driverId);
alter table PitStop add constraint FKPitStop61727 foreign key (raceId) references Race (raceId);
